import React from "react";
import FriendList from "./components/FriendList";
import AddFriend from "./components/AddFriend";
import SplitBill from "./components/SplitBill";

const initialFriends = [
  {
    id: 458,
    name: "Mustafa",
    image: "https://avatars.githubusercontent.com/u/75567558?v=4",
    balance: 0,
  },
  {
    id: 459,
    name: "Çağatay",
    image:
      "data:image/jpeg;base64,",
    balance: -25,
  },
  {
    id: 118836,
    name: "Büşra",
    image:
      "data:image/jpeg;base64,",
    balance: -7,
  },
  {
    id: 933372,
    name: "Erdinç",
    image:
      "data:image/jpeg;base64,",
    balance: 20,
  },
  {
    id: 499476,
    name: "Abdulhalim  ",
    image:
      "data:image/jpeg;base64,",
    balance: 0,
  },
];

export default function App() {
  const [friends, setFriends] = React.useState(initialFriends);
  const [selectedFriend, setselectedFriend] = React.useState(null);
  const [friendListStatus, setfriendListStatus] = React.useState(false);
  console.log("selected friend from app", selectedFriend);

  const toggleFriendList = () => {
    setfriendListStatus(!friendListStatus);
  };

  const handleSelection = (friend) => {
    if (selectedFriend == friend) setselectedFriend(null);
    else setselectedFriend(friend);
  };
  function handleAddFriend(newFriend) {
    setFriends((friends) => [...friends, newFriend]);
  }

  const [billValue, setBillValue] = React.useState("");
  const onBillChange = (e) => {
    setBillValue(+e.target.value);
  };

  const [yourExpense, setYourExpense] = React.useState("");
  const onYourExpense = (e) => {
    if (+e.target.value > billValue) {
      alert("Your expense cannot be greater than bill value");
      return;
    }
    setYourExpense(+e.target.value);
  };

  const [friendsExpense, setFriendsExpense] = React.useState("");
  const [payer, setPayer] = React.useState("you");

  const resetInputs = () => {
    setPayer("you");
    setBillValue("");
    setYourExpense("");
  };
  const handleSplitBill = (payer, yourExpense, friendExpense, billValue) => {
    if (billValue == 0 || billValue == "") {
      resetInputs();
      return;
    } else {
      calculateExpense(payer, yourExpense, friendExpense);
      setBillValue("");
      setYourExpense("");
      alert(`the bill is paid by ${payer}`);
      setPayer("you");
    }
  };

  const calculateExpense = (payer, yourExpense, friendExpense) => {
    if (payer === "you") {
      setFriends((friends) => {
        const newFriends = friends.map((friend) => {
          if (friend.id === selectedFriend.id) {
            return {
              ...friend,
              balance: friend.balance + friendExpense,
            };
          } else return friend;
        });
        return newFriends;
      });
    } else {
      setFriends((friends) => {
        const newFriends = friends.map((friend) => {
          if (friend.id === selectedFriend.id) {
            return {
              ...friend,
              balance: friend.balance - yourExpense,
            };
          } else return friend;
        });
        return newFriends;
      });
    }
  };

  return (
    <div className="app">
      <div className="sidebar">
        <FriendList
          friends={friends}
          onSelection={handleSelection}
          selectedFriend={selectedFriend}
          setselectedFriend={setselectedFriend}
          handleSplitBill={handleSplitBill}
        ></FriendList>
        <AddFriend
          onAddFriend={handleAddFriend}
          selectedFriend={selectedFriend}
          friendListStatus={friendListStatus}
          toggleFriendList={toggleFriendList}
          handleSelection={handleSelection}
          setPayer={setPayer}
          billValue={billValue}
          onBillChange={onBillChange}
          yourExpense={yourExpense}
          onYourExpense={onYourExpense}
        ></AddFriend>
      </div>
      <div className="content">
        {selectedFriend && (
          <SplitBill
            selectedFriend={selectedFriend}
            setselectedFriend={setselectedFriend}
            friendsExpense={friendsExpense}
            setFriendsExpense={setFriendsExpense}
            payer={payer}
            setPayer={setPayer}
            calculateExpense={calculateExpense}
            handleSplitBill={handleSplitBill}
            yourExpense={yourExpense}
            setYourExpense={setYourExpense}
            billValue={billValue}
            setBillValue={setBillValue}
          ></SplitBill>
        )}
      </div>
    </div>
  );
}
